import React, { useState } from 'react';
import { db } from '../services/firebase';
import { collection, addDoc } from 'firebase/firestore';
import BookCard from '../components/BookCard';
import axios from 'axios';
import { getBookSummary } from '../services/openai';
import { doc, getDoc, setDoc } from "firebase/firestore";

const Home = () => {
    const [name, setName] = useState('')
    const [genre, setGenre] = useState('');
    const [recommendedBooks, setRecommendedBooks] = useState([]);
    const [loading, setLoading] = useState(false);

    const savePreference = async () => {
        await addDoc(collection(db, 'userPreferences'), {
            name, 
            genre,
            timestamp: new Date()
        });
    };

    const fetchRecommendations = async () => {
        try {
            setLoading(true);

            const response = await axios.get(`https://openlibrary.org/subjects/${genre}.json?limit=3`);
            const rawBooks = response.data.works;

            const recommendBooks = [];

            for (const book of rawBooks) {
                const bookTitle = book.title;

                // 1. Try to fetch from Firestore first
                const summaryRef = doc(db, "summaires", bookTitle);
                const summarySnap = await getDoc(summaryRef);

                let summary;

                if (summarySnap.exists()) {
                    // Use cached summary
                    summary = summarySnap.data().text;
                } else {
                    // Get summary from OpenAPI
                    summary = await getBookSummary(bookTitle);

                    // Save to Firestore
                    await setDoc(summaryRef, {
                        text: summary,
                        timestamp: new Date()
                    });

                    // Add small delay to avoid rate-limiting
                    await new Promise(resolve => setTimeout(resolve, 1000));
                }

                recommendBooks.push({
                    title: bookTitle,
                    summary,
                    link: `https://openlibrary.org${book.key}`,
                });
            }


            setRecommendedBooks(recommendBooks);
            console.log(recommendBooks);

            //save preference and search
            await savePreference();
            await addDoc(collection(db, "searches"), {
                genre,
                timestamp: new Date()
            });
        } catch (error) {
          console.error("Error fetching recommendations:", error);
        } finally {
            setLoading(false);
        }
    };

    return (
        <div style = {{ padding: "2rem" }}>
            <h1>AI-Powered Book Reccomender</h1>
            <input
                type="text"
                value={name}
                onChange={(e) => setName(e.target.value)}
                placeholder="Enter your name"
                style={{marginRight:'10px'}}
            />
            <input 
                type="text"
                value={genre}
                onChange={ (e) => setGenre(e.target.value.toLowerCase())}
                placeholder="Enter your favorite genre (e.g., fantasy)"
            />
                <button onClick={fetchRecommendations}>Recommend Books</button>
                {loading && <p>Loading recommendations...</p>}
                <div>
                    {recommendedBooks.map((book, idx) => <BookCard key={idx} book={book} />)}
                </div>
        </div>
    );
};

export default Home;
